Layer.OnCommand:
%function(self)
				self:x(SCREEN_CENTER_X);
				self:y(SCREEN_CENTER_Y);
				self:zoomtowidth(SCREEN_WIDTH);
				self:zoomtoheight(SCREEN_HEIGHT);
				self:diffuse(.5,.5,.5,1)
				self:GetShader():uniform1f( 'yOffset', (0) )
			end

Layer.InitCommand:
%function(self)
				self:SetWidth( DISPLAY:GetDisplayWidth() );
				self:SetHeight( DISPLAY:GetDisplayHeight() );
				self:EnableDepthBuffer( true );
				self:EnableAlphaBuffer( true );
				self:EnableFloat( true );
				self:EnablePreserveTexture( true );        
				self:hidden(1);
				self:Create();
			end
