CODE.InitCommand:
%function(self)
	
		loot_curnote_alex = 1;
		
		loot_alex_notes = {}
	
		function loot_alex_spawn_note(beat,col)
		
			local color = {'red','blue','nil'}
			local d = {'Left','Down','Up','Right'}
			
			local n = 3
			if (beat/2)-(math.floor(beat/2)) == 0 then
				n = 1
			else
				n = 2
			end
			
			--Trace('Alex '..color[n]..' note '..d[col+1]);
			
			local a = loot_alex_notes[n]
			
			if not a then return end
			
			a:hidden(0);
			a:rotationz(loot_dirs[col+1])
			
		end
		
	end
