Code.InitCommand:
%function(self)

	loot_iidx_speedmod = 2.5;

	loot_curnote_iidx = 1;
	
	local loot_iidxnfptr = 1;
	loot_iidx_noteflashes = {};
	function loot_iidx_spawn_noteflash(col)
		local a = loot_iidx_noteflashes[loot_iidxnfptr];
		
		if col > 3 then col = col-4 end
		
		a:finishtweening();
		a:hidden(0);
		a:animate(1);
		a:setstate(0);
		a:blend('add');
		a:x((SCREEN_CENTER_X-245+(57*col)));
		a:y(370);
		a:zoom(1.3);
		a:linear(0.3);
		a:zoom(1.6);
		a:queuecommand('Hide');
		
		loot_iidxnfptr = loot_iidxnfptr+1
		if loot_iidxnfptr > table.getn(loot_iidx_noteflashes) then
			loot_iidxnfptr = 1
		end
	end
	
	local loot_iidxptr = 1;
	loot_iidx_notes = {};
	function loot_iidx_spawn_note(beat,col,speedmod)
	
		local a = loot_iidx_notes[loot_iidxptr];
		
		a:finishtweening();
		a:hidden(0);
		a:diffusealpha(1);
		a:animate(0);
		
		if math.mod(col,2) == 0 then a:setstate(0) else a:setstate(1) end
		
		a:x((SCREEN_CENTER_X-245+(57*col)));
		a:y(0);
		a:linear(((240/loot_bpm)/speedmod)/loot_ratemod);
		
		a:y(370*(1));
		a:sleep(0);
		a:queuecommand('Hide');
		
		loot_iidxptr = loot_iidxptr+1
		if loot_iidxptr > table.getn(loot_iidx_notes) then
			loot_iidxptr = 1
		end
		
	end
	
	loot_iidxmeasure = 400;
	local loot_iidxmptr = 1;
	loot_iidx_measures = {};
	function loot_iidx_spawn_measure(speedmod)
	
		local a = loot_iidx_measures[loot_iidxmptr];
		
		a:finishtweening();
		a:hidden(0);
		
		a:x(SCREEN_CENTER_X-160);
		a:y(0);
		a:linear(((240/loot_bpm)/speedmod)/loot_ratemod);
		
		a:y(370);
		a:queuecommand('Hide');
		
		loot_iidxmptr = loot_iidxmptr+1
		if loot_iidxmptr > table.getn(loot_iidx_measures) then
			loot_iidxmptr = 1
		end
		
	end
	
end

Layer.P1UpdateCurComboMessageCommand:
%function(self)
  if loot_comboP1 >= 100 then
	local str_cur_combo = string.format('%04d', loot_comboP1);
	local digit = string.sub(str_cur_combo, 2, 2);
	self:setstate(tonumber(digit));
  end
  self:finishtweening();
  self:zoomy(1);
  self:sleep(0.85);
  self:zoomy(0);
end

%function(self)
  if loot_comboP1 >= 10 then
	local str_cur_combo = string.format('%04d', loot_comboP1);
	local digit = string.sub(str_cur_combo, 3, 3);
	self:setstate(tonumber(digit));
  end
  self:finishtweening();
  self:zoomy(1);
  self:sleep(0.85);
  self:zoomy(0);
end

%function(self)
  if loot_comboP1 > 0 then
	local str_cur_combo = string.format('%04d', loot_comboP1);
	local digit = string.sub(str_cur_combo, 4, 4);
	self:setstate(tonumber(digit));
  end
  self:finishtweening();
  self:zoomy(1);
  self:sleep(0.85);
  self:zoomy(0);
end

